return items.map((item, index) => {
  const details = item.json.result || {};
  return {
    json: {
      phone_number: details.formatted_phone_number || "Non disponible",
      international_phone_number: details.international_phone_number || "Non disponible",
      website: details.website || "Non disponible"
    }
  };
});

const restaurants = $input.all().map(item => item.json);

let htmlContent = `
  <!DOCTYPE html>
  <html lang="fr">
  <head>
      <meta charset="UTF-8">
      <meta name="viewport" content="width=device-width, initial-scale=1.0">
      <title>Liste des Restaurants</title>
      <link href="https://fonts.googleapis.com/css2?family=Poppins:wght@400;600&display=swap" rel="stylesheet">
      <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css">
      <style>
          body {
              font-family: 'Poppins', sans-serif;
              margin: 0;
              background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
              color: #333;
          }
          .container {
              max-width: 900px;
              margin: 40px auto;
              padding: 0 20px;
          }
          h1 {
              text-align: center;
              color: #2c3e50;
              font-size: 2.5em;
              margin-bottom: 40px;
              text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.1);
          }
          .restaurant {
              background: #fff;
              border-radius: 15px;
              padding: 20px;
              margin-bottom: 30px;
              box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
              transition: transform 0.3s ease, box-shadow 0.3s ease;
              animation: fadeIn 0.5s ease-in-out;
          }
          .restaurant:hover {
              transform: translateY(-5px);
              box-shadow: 0 15px 30px rgba(0, 0, 0, 0.15);
          }
          .restaurant h2 {
              color: #e74c3c;
              font-size: 1.8em;
              margin: 0 0 10px;
          }
          .restaurant p {
              margin: 8px 0;
              font-size: 1em;
              color: #555;
              display: flex;
              align-items: center;
          }
          .restaurant p i {
              margin-right: 10px;
              color: #3498db;
              font-size: 1.2em;
          }
          .restaurant img {
              max-width: 100%;
              height: auto;
              border-radius: 10px;
              margin: 15px 0;
              box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
          }
          .website-link, .route-button {
              display: inline-block;
              margin: 10px 10px 10px 0;
              padding: 10px 20px;
              color: #fff;
              text-decoration: none;
              border-radius: 5px;
              transition: background 0.3s ease;
          }
          .website-link {
              background: #3498db;
          }
          .website-link:hover {
              background: #2980b9;
          }
          .route-button {
              background: #2ecc71;
              border: none;
              cursor: pointer;
              font-size: 1em;
          }
          .route-button:hover {
              background: #27ae60;
          }
          .map {
              height: 300px;
              width: 100%;
              margin-top: 20px;
              border-radius: 10px;
              overflow: hidden;
              box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
          }
          @keyframes fadeIn {
              from { opacity: 0; transform: translateY(20px); }
              to { opacity: 1; transform: translateY(0); }
          }
          @media (max-width: 600px) {
              .restaurant h2 { font-size: 1.5em; }
              .restaurant p { font-size: 0.9em; }
              .container { margin: 20px; }
              .website-link, .route-button { font-size: 0.9em; padding: 8px 15px; }
          }
      </style>
      <!-- Inclure l'API Google Maps -->
      <script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyD26SBDx0y_zVHM30FGrC0BiQm6RrJwpmM&callback=initMaps" defer></script>
  </head>
  <body>
      <div class="container">
          <h1>Liste des Restaurants Chinois à Antananarivo</h1>
`;

restaurants.forEach((restaurant, index) => {
  htmlContent += `
    <div class="restaurant">
        <h2>${restaurant.name || 'Nom inconnu'}</h2>
        <p><i class="fas fa-map-marker-alt"></i><strong>Adresse :</strong> ${restaurant.adresse || 'Non disponible'}</p>
        <p><i class="fas fa-phone"></i><strong>Téléphone :</strong> ${restaurant.phone_number || 'Non disponible'}</p>
        <p><i class="fas fa-globe"></i><strong>Téléphone international :</strong> ${restaurant.international_phone_number || 'Non disponible'}</p>
        ${
          restaurant.website
            ? `<a href="${restaurant.website}" class="website-link" target="_blank"><i class="fas fa-link"></i> Visiter le site web</a>`
            : '<p><i class="fas fa-link"></i><strong>Site web :</strong> Non disponible</p>'
        }
        <p><i class="fas fa-map-pin"></i><strong>Coordonnées :</strong> Latitude ${restaurant.latitude || 'N/A'}, Longitude ${restaurant.longitude || 'N/A'}</p>
        <img src="${restaurant.photo && restaurant.photo !== 'Pas de photo disponible' ? restaurant.photo : 'https://via.placeholder.com/400'}" alt="Photo de ${restaurant.name || 'restaurant'}">
        <!-- Bouton pour afficher l'itinéraire -->
        <button class="route-button" onclick="showRoute(${index})"><i class="fas fa-route"></i> Voir l'itinéraire</button>
        <!-- Conteneur pour la carte -->
        <div id="map-${index}" class="map"></div>
    </div>
  `;
});

htmlContent += `
      </div>
      <script>
          // Tableau pour stocker les données des cartes
          const mapData = ${JSON.stringify(
            restaurants.map(restaurant => ({
              lat: parseFloat(restaurant.latitude) || 0,
              lng: parseFloat(restaurant.longitude) || 0,
              name: restaurant.name || 'Restaurant'
            }))
          )};

          // Tableau pour stocker les instances de carte et de rendu d'itinéraire
          const maps = [];
          const directionsRenderers = [];

          // Fonction pour initialiser les cartes
          function initMaps() {
              mapData.forEach((data, index) => {
                  // Vérifier si les coordonnées sont valides
                  if (data.lat !== 0 && data.lng !== 0) {
                      const map = new google.maps.Map(document.getElementById('map-' + index), {
                          center: { lat: data.lat, lng: data.lng },
                          zoom: 15,
                          mapTypeId: 'roadmap'
                      });
                      // Ajouter un marqueur
                      new google.maps.Marker({
                          position: { lat: data.lat, lng: data.lng },
                          map: map,
                          title: data.name
                      });
                      // Stocker la carte et un renderer pour les itinéraires
                      maps[index] = map;
                      directionsRenderers[index] = new google.maps.DirectionsRenderer({
                          map: map,
                          suppressMarkers: false // Garder les marqueurs pour l'origine et la destination
                      });
                  } else {
                      // Cacher le conteneur de la carte si les coordonnées ne sont pas valides
                      document.getElementById('map-' + index).style.display = 'none';
                      document.querySelector('#map-' + index + ' + .route-button').style.display = 'none';
                  }
              });
          }

          // Fonction pour afficher l'itinéraire
          function showRoute(index) {
              if (!maps[index]) return;

              // Vérifier si la géolocalisation est disponible
              if (navigator.geolocation) {
                  navigator.geolocation.getCurrentPosition(
                      (position) => {
                          const userLocation = {
                              lat: position.coords.latitude,
                              lng: position.coords.longitude
                          };
                          const destination = {
                              lat: mapData[index].lat,
                              lng: mapData[index].lng
                          };

                          // Initialiser le service de calcul d'itinéraire
                          const directionsService = new google.maps.DirectionsService();
                          directionsService.route(
                              {
                                  origin: userLocation,
                                  destination: destination,
                                  travelMode: google.maps.TravelMode.DRIVING // Mode de transport (peut être modifié)
                              },
                              (result, status) => {
                                  if (status === google.maps.DirectionsStatus.OK) {
                                      // Afficher l'itinéraire sur la carte
                                      directionsRenderers[index].setDirections(result);
                                  } else {
                                      alert('Impossible de calculer l\\'itinéraire : ' + status);
                                  }
                              }
                          );
                      },
                      (error) => {
                          alert('Erreur de géolocalisation : ' + error.message);
                      }
                  );
              } else {
                  alert('La géolocalisation n\\'est pas prise en charge par votre navigateur.');
              }
          }
      </script>
  </body>
  </html>
`;

return [{
  json: { fileName: 'restaurants.html' },
  binary: {
    data: {
      data: Buffer.from(htmlContent).toString('base64'),
      mimeType: 'text/html',
      fileName: 'restaurants.html'
    }
  }
}];

// Combiner tous les restaurants de tous les items
const allRestaurants = items
  .flatMap(item => item.json.results || [])
  .map(restaurant => ({
    json: {
      name: restaurant.name || "Nom inconnu",
      latitude: restaurant.geometry?.location?.lat || 0,
      longitude: restaurant.geometry?.location?.lng || 0,
      vicinity: restaurant.formatted_address || "Adresse inconnue",
      photo: restaurant.photos?.[0]?.photo_reference 
        ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photo_reference=${restaurant.photos[0].photo_reference}&key=VOTRE_CLE_API`
        : "Pas de photo disponible",
      place_id: restaurant.place_id || "", // Conserver le place_id
      icon: restaurant.icon || "Sans icone"
    }
  }));

return allRestaurants;
